<?php include_once('../../system/init.php'); ?> <?php header("content-type: application/x-javascript"); ?>$(document).ready(function() {	var user_site_count = <? echo user_site_count(); ?>;	var mainContainer = $("#main");	var currentPage = 'dashboard';	var lastPage = '';	//updateUrl(currentPage);	//loadPage(page);	$('div.page-container').find('.active').css('display', 'block');	$("a[data-dash-page]").click(function(e) {		e.preventDefault();		if(currentPage != $(this).data('dash-page')){			lastPage = currentPage;			currentPage = $(this).data('dash-page');			changePage(currentPage);		}	});	$('a[data-action]').click(function(e) {		e.preventDefault();		switch($(this).data('action')){			case 'add_site':				<? $account_type = userinfo('acct_type'); ?>				<?php					switch($account_type) {						case 0: ?>							alert("YOU CANT ADD WEBSITE!");						<? break;						case 1: ?>							<? if(user_site_count() <= 5) : ?>								var number = 1 + Math.floor(Math.random() * 500);								$('#sites-list-body tr:last').after('<tr><td>New Site - '+ number +'</td></tr>');							<? else : ?>							<? endif; ?>						<? break;					}				?>			break;		}	});	function changePage() {		$('#'+lastPage).fadeOut('200',function() {			$('#'+currentPage).fadeIn('300');		});	}});