<?php include_once('../../system/init.php'); ?> <?php header("content-type: application/x-javascript"); ?>$(document).ready(function() {	var user_site_count = sites_num();	var mainContainer = $("#main");	var currentPage = 'dashboard';	var lastPage = '';	//updateUrl(currentPage);	$('#site_pane').hide();	//loadPage(page);	$('div.page-container').find('.active').css('display', 'block');	$("a[data-dash-page]").click(function(e) {		e.preventDefault();		if(currentPage != $(this).data('dash-page')){			lastPage = currentPage;			currentPage = $(this).data('dash-page');			changePage(currentPage);		}	});	$('a[data-action=create-site]').click(function(e) {		e.preventDefault();		var site_title = $('#createSiteModal #site-title').val();		$.ajax({			type: "POST",			url: "system/request/post.php",			data: {request:"create_site", title:site_title},			dataType: "json"		}).done(function(msg){			$('#createSiteModal').modal('hide');			$('#sites-list-body tr:last').after('<tr><td><a href="#">'+ msg.site +'</a></td></tr>');		});	});	$('a[data-site-id]').click(function(e) {		e.preventDefault();		updateSitePane($(this).data('site-id'));	});	$('#pane-site-title').editable('system/request/post.php', {indicator: 'Saving...', tooltip: 'Click to Edit Site title.'});	function changePage() {		$('#'+lastPage).fadeOut('200',function() {			$('#'+currentPage).fadeIn('300');		});	}	function updateSitePane(id) {		$('#site_pane').show();		$.ajax({			type: "POST",			url: "system/request/get.php",			data: {request:"site_data",site_id:id},			dataType: "json"		}).done(function(msg) {			$('#pane-site-title').html(msg.data.title);		});	}	function sites_num() {		$.ajax({			type:"POST",			url: "system/request/get.php",			data: {request:"num_sites"}		}).done(function(msg) {			return msg;		});	}});