<?php include_once('../../system/init.php'); ?> <?php header("content-type: application/x-javascript"); ?>$(document).ready(function() {	var user_site_count = <? echo user_site_count(); ?>;	var acct_type = <? echo userinfo('acct_type'); ?>;	var mainContainer = $("#main");	var currentPage = 'dashboard';	var lastPage = '';	//updateUrl(currentPage);	//loadPage(page);	$('div.page-container').find('.active').css('display', 'block');	$("a[data-dash-page]").click(function(e) {		e.preventDefault();		if(currentPage != $(this).data('dash-page')){			lastPage = currentPage;			currentPage = $(this).data('dash-page');			changePage(currentPage);		}	});	$('a[data-action]').click(function(e) {		e.preventDefault();		switch($(this).data('action')){			case 'add_site':			break;		}	});	$('a[data-site-id]').click(function(e) {		e.preventDefault();		updateSitePane($(this).data('site-id'));	});	function changePage() {		$('#'+lastPage).fadeOut('200',function() {			$('#'+currentPage).fadeIn('300');		});	}	function updateSitePane(id) {		$.ajax({			type: "POST",			url: "system/request/get.php",			data: {request:"site_data",site_id:id}		}).done(function(msg) {			$('#site_pane').html(msg);		});	}	function sites_num() {		$.ajax({			type:"POST",			url: "system/request/get.php",			data: {request:"num_sites"}		}).done(function(msg) {			return msg;		});	}});