<?php header("content-type: application/x-javascript"); ?>$(document).ready(function() {	var user_site_count = 1;	var mainContainer = $("#main");	var currentPage = 'dashboard';	var lastPage = '';	//updateUrl(currentPage);	//loadPage(page);	$('div.page-container').find('.active').css('display', 'block');	$("a[data-dash-page]").click(function(e) {		e.preventDefault();		if(currentPage != $(this).data('dash-page')){			lastPage = currentPage;			currentPage = $(this).data('dash-page');			changePage(currentPage);		}	});	function changePage() {		$('#'+lastPage).fadeOut('200',function() {			$('#'+currentPage).fadeIn('300');		});	}	});