<?php 	include_once("../../system/init.php");	header("content-type: application/x-javascript"); ?>$(document).ready(function() {		var user_site_count = <? echo user_site_count(); ?>;	var main = $("#main");		$('#logout').click(function(event) {		event.preventDefault();		$.post('system/request/post.php', {request:"logout"}, function(response) {			response = jQuery.parseJSON(response);			console.log(response);			if (response.status == 'success'){				switch(response.trigger) {					case '0':						setTimeout("location.href = 'index.php';", 500);					break;				}			}else if(response.status == 'error'){				alert('There was an error: ' + response.type);			}					})	});});